const valid_credit_card = require('./valid_credit_card');

// Test edilecek kredi kartı numaraları
const testNumbers = [
    "79927398713",   
    "1234567812345670", 
    "4111111111111111"  
];

testNumbers.forEach(number => {
    const isValid = valid_credit_card(number);
    console.log(`Testing card number ${number}: ${isValid ? "Valid" : "Invalid"}`);
});
